            <div class="input-group mb-5">
                <span class="input-group-text" id="basic-addon3">https://example.com/users/</span>
                <input type="text" class="form-control" id="basic-url" aria-describedby="basic-addon3">
            </div>
            <!--end::Input group-->

            <!--begin::Input group-->
